  const edgeGroups: Record<string, EdgeGroup> = {}

  // make edge groups that between same nodes
  const map: Record<string, Edges> = {}
  for (const [id, edge] of Object.entries(edges)) {
    if (!(edge.source in nodes && edge.target in nodes)) {
      // reject if no node ID is found on the nodes
      continue
    }
    const key = [edge.source, edge.target].sort().join("<=>")
    const values = map[key] || {}
    values[id] = edge
    map[key] = values
  }

  // Calculate the following:
  // - the starting point of each line
  // - the width between the centers of the lines at both ends
  // *Note*: the drawing position of the line is the center of the line.
  const calcGap =
    configs.edge.gap instanceof Function
      ? configs.edge.gap
      : (_e: Edges, _c: Configs) => configs.edge.gap as number
  for (const [key, edges] of Object.entries(map)) {
    const edgeLen = Object.keys(edges).length
    if (edgeLen == 0) continue

    const gap = calcGap(edges, configs)
    const [edgeId, edge] = Object.entries(edges)[0]
    if (edgeLen === 1) {
      edgeLayoutPoints[edgeId] = { edge, pointInGroup: 0, groupWidth: 0 }
      edgeGroups[key] = { edges, groupWidth: 0, summarize: false }
    } else {
      let pointInGroup = 0
      const lineHalfWidths = Object.entries(edges).map(([id, edge]) => {
        let width = Config.value(configs.edge.normal.width, edge)
        if (isNaN(+width)) {
          console.warn(
            "[v-network-graph] Edge width is invalid value. id=[%s] value=[%s]",
            id,
            width
          )
          width = 1
        }
        return width / 2
      })
      const points = Object.entries(edges).map(([edgeId, edge], i) => {
        if (i > 0) {
          pointInGroup += lineHalfWidths[i - 1] + gap + lineHalfWidths[i]
        }
        return [edgeId, edge, pointInGroup] as [string, Edge, number]
      })
      const groupWidth = pointInGroup
      points.forEach(([edgeId, edge, pointInGroup]) => {
        edgeLayoutPoints[edgeId] = { edge, pointInGroup, groupWidth }
      })
      edgeGroups[key] = { edges, groupWidth, summarize: false }
    }
  }

  return { edgeLayoutPoints, edgeGroups }
}

function defaultCheckSummarize(nodes: Nodes, edges: Edges, configs: Configs, width: number) {
  // aggregate if the edge width and gap width exceed the size of the node
  const edgeCount = Object.entries(edges).length
  if (edgeCount === 1) return false

  // const width =
  //   Object.values(edges)
  //     .map(e => Config.value(configs.edge.normal.width, e))
  //     .reduce((sum, v) => sum + v, 0) +
  //   configs.edge.gap * (edgeCount - 1)

  const minWidth = Math.min(
    ...Object.values(edges)
      .flatMap(e => [nodes[e.source], nodes[e.target]])
      .filter(v => v)
      .map(node => {
        const shape = Config.values(configs.node.normal, node)
        if (shape.type === "circle") {
          return shape.radius * 2
        } else {
          return Math.min(shape.width, shape.height)
        }
      })
  )
  return width > minWidth
}

function calculateEdgePositionInner(
  edge: Edge,
  source: Position | undefined,
  target: Position | undefined,
  scale: number,
  groupWidth: number,
  pointInGroup: number,
  keepOrder: EdgeKeepOrderType
): LinePosition {
  let x1, y1, x2, y2
  if (edge.source < edge.target) {
    ;[x1, y1, x2, y2] = calculateLinePosition(
      source?.x ?? 0,
      source?.y ?? 0,
      target?.x ?? 0,
      target?.y ?? 0,
      scale,
      groupWidth,
      pointInGroup,
      keepOrder
    )
  } else {
    ;[x2, y2, x1, y1] = calculateLinePosition(
      target?.x ?? 0,
      target?.y ?? 0,
      source?.x ?? 0,
      source?.y ?? 0,
      scale,
      groupWidth,
      pointInGroup,
      keepOrder
    )
  }
  return { p1: { x: x1, y: y1 }, p2: { x: x2, y: y2 } }
}

function calculateLinePosition(
  x1: number,
  y1: number,
  x2: number,
  y2: number,
  scale: number,
  groupWidth: number,
  pointInGroup: number,
  keepOrder: EdgeKeepOrderType
): [number, number, number, number] {
  const dx = x2 - x1
  const dy = y2 - y1

  // Shifting width from center
  let diff = (groupWidth / 2 - pointInGroup) * scale

  // Adjust the relative position.
  if (diff !== 0 && keepOrder !== "clock") {
    const radian = Math.atan2(y2 - y1, x2 - x1)
    if (keepOrder === "vertical") {
      // Keep the vertical alignment of multiple edges.
      const perpendicular = Math.PI / 2
      if (radian < -perpendicular || radian >= perpendicular) {
        diff *= -1
      }
    } else if (keepOrder === "horizontal") {
      // Keep the horizontal alignment of multiple edges.
      if (radian < 0) {
        diff *= -1
      }
    }
  }

  if (dx === 0) {
    const sign = dy < 0 ? -1 : 1
    return [x1 + diff * sign, y1, x2 + diff * sign, y2]
  } else if (dy === 0) {
    const sign = dx < 0 ? 1 : -1
    return [x1, y1 + diff * sign, x2, y2 + diff * sign]
  } else {
    const slope = dy / dx
    const moveSlope = -1 / slope
    if (dy < 0) {
      diff = -diff
    }
    const diffX = diff / Math.sqrt(1 + Math.pow(moveSlope, 2))
    return [x1 + diffX, y1 + diffX * moveSlope, x2 + diffX, y2 + diffX * moveSlope]
  }
}
import { Point2D } from "./core"

export function add(v1: Point2D, v2: Point2D): Point2D
export function add<T extends Point2D>(v1: Point2D, v2: Point2D, target: T): T
export function add(v1: Point2D, v2: Point2D, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  target.x = v1.x + v2.x
  target.y = v1.y + v2.y
  return target
}

export function subtract(v1: Point2D, v2: Point2D): Point2D
export function subtract<T extends Point2D>(v1: Point2D, v2: Point2D, target: T): T
export function subtract(v1: Point2D, v2: Point2D, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  target.x = v1.x - v2.x
  target.y = v1.y - v2.y
  return target
}

export function multiply(v1: Point2D, v2: Point2D): Point2D
export function multiply<T extends Point2D>(v1: Point2D, v2: Point2D, target: T): T
export function multiply(v1: Point2D, v2: Point2D, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  target.x = v1.x * v2.x
  target.y = v1.y * v2.y
  return target
}

export function multiplyScalar(v: Point2D, scalar: number): Point2D
export function multiplyScalar<T extends Point2D>(v: Point2D, scalar: number, target: T): T
export function multiplyScalar(v: Point2D, scalar: number, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  target.x = v.x * scalar
  target.y = v.y * scalar
  return target
}

export function divide(v1: Point2D, v2: Point2D): Point2D
export function divide<T extends Point2D>(v1: Point2D, v2: Point2D, target: T): T
export function divide(v1: Point2D, v2: Point2D, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  target.x = v1.x / v2.x
  target.y = v1.y / v2.y
  return target
}

export function dot(v1: Point2D, v2: Point2D): number {
  return v1.x * v2.x + v1.y * v2.y
}

export function cross(v1: Point2D, v2: Point2D): number {
  return v1.x * v2.y - v1.y * v2.x
}

export function lengthSquared(v: Point2D): number {
  return v.x * v.x + v.y * v.y
}

export function length(v: Point2D): number {
  return Math.sqrt(lengthSquared(v))
}

export function distanceSquared(v1: Point2D, v2: Point2D): number {
  const dx = v1.x - v2.x
  const dy = v1.y - v2.y
  return dx * dx + dy * dy
}

export function distance(v1: Point2D, v2: Point2D): number {
  return Math.sqrt(distanceSquared(v1, v2))
}

export function normalize(v: Point2D): Point2D
export function normalize<T extends Point2D>(v: Point2D, target: T): T
export function normalize(v: Point2D, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  const len = length(v)
  if (len === 0) {
    target.x = 1
    target.y = 0
  } else {
    divide(v, { x: len, y: len }, target)
  }
  return target
}

export function rotate(v: Point2D, angle: number): Point2D
export function rotate<T extends Point2D>(v: Point2D, angle: number, target: T): T
export function rotate(v: Point2D, angle: number, target?: Point2D): Point2D {
  if (!target) {
    target = { x: 0, y: 0 }
  }
  // rotate in radians CCW from +X axis
  const newX = v.x * Math.cos(angle) - v.y * Math.sin(angle)
  const newY = v.x * Math.sin(angle) + v.y * Math.cos(angle)
  target.x = newX
  target.y = newY
  return target
}

const DEGREES = 180 / Math.PI

function rad2deg(rad: number) {
  return rad * DEGREES
}

export function angle(v: Point2D) {
  return Math.atan2(v.y, v.x)
}

export function angleDegree(v: Point2D) {
  return rad2deg(angle(v))
}
